import _ from 'lodash';

/**
 * Get index of an searchCriteria in a collection of nodes or a parent searchCriteria
 * @param collection
 * @param searchCriteria
 */
const index = function (collection, searchCriteria) {

    let index = -1;

    // if collection parameter is an element, assume we want to find it's place among the other children of it's parent.
    if (_.isElement(collection)) {

        searchCriteria = collection;
        collection = Array.from(collection.parentNode.children);
    }

    // check the collection is an array
    if (_.isArray(collection)) {

        // search as literal object
        if (_.isElement(searchCriteria)) {
            index = _.indexOf(collection, searchCriteria);
            // search as CSS selector
        } else if (_.isString(searchCriteria)) {
            index = _.findIndex(collection, function (item) {
                return item.matches(searchCriteria);
            });
        } else {
            throw new Error('Search criteria must be an element or a String');
        }
    } else {
        throw new Error('Trying to get an index in a non-Array collection')
    }

    return index;

};

export default index;

/**
 *
 * Remove multiple classes from an Element. Support removing classes with wildcards
 *
 * @param element DOM element to remove classes from
 * @param patterns one or more class patterns. Patterns may be regular class names but can also contain `*` wildcards
 */
const removeClasses = function (element, ...patterns) {

    // save classes to avoid transforming the classname of the element multiple times
    var classes = element.className;

    patterns.forEach( (pattern) => {

        let patternRegex = pattern.replace('*', '[a-zA-Z0-9-]+');
        patternRegex += '\\s?';

        patternRegex = new RegExp(patternRegex);

        classes = classes.replace( patternRegex, '' )

    } );

    // re assign new classes
    element.className = classes;

};

export default removeClasses;

/**
 * Turns a string representing one or more DOM elements into usable objects.
 * @param {string} sourceString
 * @returns {Element|DocumentFragment} The DOM representation of the string. If multiple root tags are present in the
 *     string, it will return a DocumentFragment
 */
const stringToElement = function (sourceString) {
    // create a dummy element that is used
    let elementIterator = document.createElement("div");
    let i;

    elementIterator.innerHTML = sourceString;

    if ( elementIterator.children.length > 1 ) { // return document fragment
        let docFrag = document.createDocumentFragment();
        while (i = elementIterator.firstChild ) {
            docFrag.appendChild(i)
        }

        return docFrag;

    } else { // just return first element
        return elementIterator.firstElementChild;
    }

};

export default stringToElement;

