/**
 *
 * PreloaderQueue
 *
 * Simple Event & Promise based queue mechanism for loading multiple preloaders
 *
 */
class LoaderQueue extends EventTarget {

    constructor(urls = []) {
        super();

        // create an array of prelaoders for each url
        this.loaders = [];

        urls.forEach( (url) => {
            this.add( url );
        });

        // Create promise and save it's callbacks to this instance for later access
        this.promise = new Promise( (resolve, reject) => {
            this._promiseResolve = resolve;
            this._promiseReject = reject;
        });

        // For convenience, force trigger a progress event when there are no loads complete
        // use setImmediate (probably with polyfill) to force triggering on next event loop (otherwise no listeners will have time to be added)
        setImmediate( () => this._preloaderCompleteHandler() );

    }

    add(preloaderOrUrl) {

        // determine if we should pause the loader based on how many are currently loading
        let shouldPause = this.loaders.reduce( (total, loader) => { return total + (loader.status == Loader.STATUS.LOADING ? 1 : 0)}, 0) >= LoaderQueue.MAX_CONCURRENT;

        // create preloader
        let preloader = ( _.isString(preloaderOrUrl) ) ? new Loader(preloaderOrUrl, {paused: shouldPause} ) : preloaderOrUrl;

        // Attach events
        preloader.on('complete', this._preloaderCompleteHandler, this);
        preloader.on('progress', this._preloaderProgressHandler, this);

        this.loaders.push( preloader );

        return preloader;
    }

    get progress() {

        if (this.loaders.length == 0) {
            return -1;
        } else {
            let totalProgress = this.loaders.reduce( (total, loader) => {return total + loader.progress}, 0);
            return totalProgress / this.loaders.length;
        }

    }

    _preloaderProgressHandler(event) {
        this.trigger('progress', {progress : this.progress});
    }

    _preloaderCompleteHandler() {

        // get number of complete loaders and total loaders in queue
        var totalLoaders = this.loaders.length;

        var completeLoaders = 0;
        var currentLoaders = 0;
        var currentProgress = 0;

        // Count complete and current loaders, total their progress
        this.loaders.forEach( loader => {

            if (loader.status == Loader.STATUS.COMPLETE) {
                completeLoaders++;
            } else if (loader.status == Loader.STATUS.LOADING) {
                currentLoaders++;
            }

        });

        // Trigger events for progress and completion
        if (completeLoaders === totalLoaders) {
            this._promiseResolve(this.loaders);
            this.trigger('complete'); // TODO return loaders? data?
        } else {
            this.trigger('progress', {progress: this.progress});

            // if there are less current loaders than MAX_CONCURRENT, unpause them
            if (currentLoaders < LoaderQueue.MAX_CONCURRENT) {

                /*
                Big one liner to unpause queued loaders :
                - filters for paused loaders
                - takes as many as possible, below MAX_CONCURRENT threshold
                - starts load() on each Preloader instance
                 */
                this.loaders.filter( loader => loader.status == Loader.STATUS.PAUSED ).slice(0, LoaderQueue.MAX_CONCURRENT - currentLoaders).forEach(loader => loader.load() );

            }

        }


    }

}

LoaderQueue.MAX_CONCURRENT = 5;

export {Loader as default, LoaderQueue};

const AspectRatio = {

    FIT_MODE: {
        COVER: 'cover',
        CONTAIN: 'contain'
    },

    /**
     * Fits content to containers, either covering or containing them
     * @param content object describing a rectangle
     * @param container object describuing a rectangle
     * @param fitMode cover or contain content in container. refers to static properties of AspectRatio object
     */
    fitContent : function (content, container, fitMode = AspectRatio.FIT_MODE.COVER) {

        // calc ratios to decide how to do covering or containing
        const contentRatio = content.width / content.height;
        const containerRatio = container.width / container.height;

        let computedWidth;
        let computedHeight;

        // fit to width / height
        if ( (contentRatio < containerRatio && fitMode == AspectRatio.FIT_MODE.COVER) || (contentRatio > containerRatio && fitMode == AspectRatio.FIT_MODE.CONTAIN) ) {
            computedWidth   = container.width;
            computedHeight  = container.width / contentRatio;
        } else {
            computedWidth   = container.height * contentRatio;
            computedHeight  = container.height;
        }

        const computedY = (container.height - computedHeight) / 2;
        const computedX = (container.width - computedWidth) / 2;

        // return new rect with proper values
        return {
            y : computedY,
            x : computedX,
            width : computedWidth,
            height : computedHeight
        };

    }

};


export default AspectRatio;

import _ from "lodash";

// browser prefixes
const prefixes = [
    'Webkit',
    'Moz',
    'MS',
    'O'
];

// Cache of modifier functions once the proper prefix has been found
const prefixedModifierCache = {};

// Test element for finding prefixed properties
const prefixTestEl = document.createElement('div');

/**
 * Create a prefixed aware CSS modifier for a property.
 *
 * Generated modifiers are stored in the const above
 *
 * @param property
 * @returns {*}
 */
function createModifier(property) {
    // prefixed properties are camel cased
    let propertyCap = _.capitalize(property);

    // get all prefixed properties (and un-prefixed in first position)
    let prefixedProperties = [property].concat( _.map(prefixes, function(prefix){ return prefix + propertyCap}) );

    // find the first acceptable property name
    let prefixedPropName = _.find(prefixedProperties, function(prop){ return prop in prefixTestEl.style });

    // cache modifier to object
    prefixedModifierCache[property] = function(targetStyle, value) {
        targetStyle[prefixedPropName] = value;
    };

    return prefixedModifierCache[property];
}

/**
 * Style util
 *
 * @type {{set: Style.set, get: Style.get, formatValue: Style.formatValue}}
 */
const Style = {

    set : function (element, property, value) {

        let elementStyles =  ( _.isElement(element) ) ? element.style : element;

        // internally we always use an object and loop over its keys and values
        let propObj;

        // convert string to use in object, or just use object
        if (_.isString(property)) {
            propObj = {};
            propObj[property] = value
        } else {
            propObj = property;
        }

        // treat property as an object of csspropertyname : value pairs
        for ( let prop in propObj) {
            if (propObj.hasOwnProperty(prop)) {

                // if a the value of this property isn't already a string, format it.
                let specifiedValue = propObj[prop];
                let finalValue;

                if (specifiedValue !== null) {
                    finalValue = _.isString(specifiedValue) ? specifiedValue : this.formatValue(prop, specifiedValue);
                } else {
                    finalValue = null;
                }


                // get cached or new modifier function
                ( prefixedModifierCache[prop] || createModifier(prop) )(elementStyles, finalValue);
            }
        }

    },

    /**
     * Get style applied to this element
     *
     * TODO check on element.style object if we can detect !important rules someway
     *
     * @param element
     * @param property
     * @returns {*}
     */
    get : function(element, property) {
        return window.getComputedStyle( element )[ property ];
    },

    /**
     *
     * @param property name of the CSS property
     * @param value value of the property that isn't a String
     * @param units
     * @returns {*}
     */
    formatValue : function(property, value, units = 'px') {

        let formattedValue;

        switch(property) {
            case 'top' :
            case 'right' :
            case 'bottom' :
            case 'left' :
            case 'minWidth' :
            case 'minHeight' :
            case 'width' :
            case 'height' :
                formattedValue = value.toString() + units;
                break;

            case 'opacity' :
                formattedValue = value;
                break;
        }

        // format to empty string
        if ( formattedValue === null) formattedValue = '';

        return formattedValue;
    }

};

export default Style;

import _            from "lodash";
import EventTarget  from "../core/EventTarget";

// Cross platform function to get scroll position
const getScrollTop = function () {
    return window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop || 0;
};

let WindowManager;

class WindowManagerClass extends EventTarget{

    constructor() {
        // Singleton check
        if ( WindowManager ) {
            throw new Error("WindowManager is a Singleton, an instance already exists");
        }

        super();
    }

    /* ==========================

     Initialization function

     ========================== */
    initialize (config) {

        if ( !this.initialized ) {
            this.config = _.defaults(config || {}, WindowManagerClass.defaultConfig);

            this.breakpoints = this.config.breakpoints;

            // Create and save Event Handlers
            this._domEvents = {};
            this._domEvents.resize  = _.throttle(_.bind(this._resizeHandler, this), this.config.resizeThrottle);
            this._domEvents.load    = _.bind(this._loadHandler, this);
            this._domEvents.unload  = _.bind(this._unloadHandler, this);
            this._domEvents.scroll  = _.throttle(_.bind(this._scrollHandler, this), this.config.scrollThrottle);

            // listen for events
            window.addEventListener('resize', this._domEvents.resize);
            window.addEventListener('load',   this._domEvents.load);
            window.addEventListener('unload', this._domEvents.unload);

            this.bindScrollEvent();

            // set up variable tracking
            this.scrollPosition = this.scrollPosition || {top: getScrollTop()};

            // cache window metrics now
            this._updateMetrics();
            this._updateScrollMetrics();

            this._detectBreakpoint(true);

            this.initialized = true;

        }

    }

    /* ==========================

     Public Members

     ========================== */

    // Allow binding & unbinding of scroll events
    bindScrollEvent() {
        window.addEventListener('scroll', this._domEvents.scroll);
    }

    unbindScrollEvent() {
        window.removeEventListener('scroll', this._domEvents.scroll);
    }

    scrollTo(y = 0, x = 0) {
        window.scrollTo(y, x);

        this.scrollPosition.top = y;
        this.scrollPosition.left = x;
    }

    /**
     * Determine if the viewport is of a a minimum width
     *
     * @param {Object|String|Number} breakpoint. If of type object, assumes to be a named breakpoint object with a value propert, if a string, a named breakpoint's name, if a number the breakpoint's value. A number maybe used to arbitrarily check window width
     * @returns {boolean}
     */
    minWidth(breakpoint) {

        // Get numerical value for breakpoint
        let breakpointValue;
        if ( _.isNumber(breakpoint) ) {
            breakpointValue = breakpoint;
        } else if ( _.isString(breakpoint) ) {
            breakpointValue = _.find( this.breakpoints, {name: breakpoint}).value;
        } else if ( _.isObject(breakpoint) ) {
            breakpointValue = breakpoint.value;
        }

        return this.width >= breakpointValue;
    }

    /* ==========================

     Private Members

     ========================== */
    _updateMetrics() {
        this.width = window.innerWidth;
        this.height = window.innerHeight;
    }

    _updateScrollMetrics() {
        this.scrollPosition.top = getScrollTop();
    }

    /**
     * Called on window resize events to detect which breakpoint we're on
     *
     * @param supressEvents
     * @private
     */
    _detectBreakpoint(supressEvents) {

        // find biggest matching BP
        var breakpoint = _.findLast(this.breakpoints, (bp) => {
            return this.width >= bp.value;
        });

        // ony if the breakpoint has changed
        if (this.currentBreakpoint != breakpoint) {
            let previousBreakpoint = this.currentBreakpoint;
            this.currentBreakpoint = breakpoint;

            // dispatch event for breakpoint, simply cloning the BP object for the event data
            if (!supressEvents) {
                this.trigger('breakpoint', {breakpoint: _.clone(breakpoint), previous : _.clone(previousBreakpoint)}); // TODO make breakpoints immutable
            }
        }

    }

    /* ==========================

     Event Handlers

     ========================== */
    _scrollHandler(event) {
        var previousTop = this.scrollPosition.top;
        this._updateScrollMetrics();
        var currentTop = this.scrollPosition.top;

        // determine direction by comparing previous scroll position
        var direction = (previousTop < currentTop) ? 1 : -1;

        // trigger custom event
        this.trigger('scroll', {scrollPosition: this.scrollPosition, direction : direction, originalEvent: event});
    }

    _resizeHandler(event) {
        this._updateMetrics();
        this.trigger('resize', {width: this.width, height: this.height});
        this._detectBreakpoint();
    }

    _unloadHandler() {
        // window.scrollTo(0, 0);
    }

    _loadHandler() {
        this.trigger('load');
    }

}

WindowManagerClass.defaultConfig = {
